/* Appearance Mode */
function webTheme(e){document.body.classList.remove("lgt","drk","sym"),document.body.classList.add(e),localStorage.setItem("themeChoice",e)}function modeL(){webTheme("lgt"),localStorage.setItem("themeChoice","light")}function modeD(){webTheme("drk"),localStorage.setItem("themeChoice","dark")}function modeS(){let e=window.matchMedia("(prefers-color-scheme: dark)").matches;e?(webTheme("drk"),document.body.classList.add("sym")):(webTheme("lgt"),document.body.classList.add("sym")),localStorage.setItem("themeChoice","sym")}function loadUserThemePreference(){let e=localStorage.getItem("themeChoice");"light"===e?modeL():"dark"===e?modeD():modeS()}window.onload=loadUserThemePreference;